INSERT INTO public.inline_menu ("language_id", "shelter_id", "tag_callback", "question", "answer", "button", "state_id", "state_id_next", "priority")
VALUES
    (1, 1, E'1_1_start', E'/start', E'Добро пожаловать в приют для собак. Выберите пункт меню', NULL, 1, 1, 0),
    (1, 1, E'1_general_info', NULL, E'Выберите пункт меню', E'Общая информация о приюте.', 1, 2, 1),
    (1, 1, E'1_how_to', NULL, E'Выберите пункт меню', E'Как взять собаку из приюта.', 1, 4, 2),
    (1, 1, E'1_report', NULL, E'Выберите пункт меню', E'Отправить отчет о собаке.', 1, 5, 3),
    (1, 1, E'1_volunteer', NULL, E'К вам уже спешат!!!', E'Позвать волонтера.', 1, -5, 4),
    (1, 1, E'2_back_to_info_menu', NULL, E'Добро пожаловатьв приют для собак. Выберите пункт меню', E'Назад.', 2, 1, 7),
    (1, 1, E'2_about_ru', NULL, E'Приют Доброе сердце - это муниципальный приют для бездомных собак и кошек в Южном округе г. Москвы. В нем живет почти 2500 собак и 150 кошек. Большие и маленькие, пушистые и гладкие, веселые и задумчивые - и на всех одна большая мечта - встретить своего Человека и найти Дом.\n\nВзять домой\n\nЕсли вы хотите взять собаку или кошку, не ищите питомник, в котором можно купить щенка или котенка - просто свяжитесь с нами, и вы обязательно найдете себе самого лучшего друга. Во всем мире это уже стало доброй традицией - человек, который решил завести питомца, обращается в приют, чтобы подарить заботу и любовь тому, кто уже появился на свет, но ему почему-то досталась нелегкая судьба. Мы поможем вам выбрать животное с учетом ваших пожеланий и предпочтений, с радостью познакомим со всеми нашими собаками и кошками. Все наши питомцы привиты и стерилизованы.\n\n         Собачий приют в Доброе сердце - это не питомник, поэтому у нас не бывает породистых собак с родословной. Но беспородных щенков, подростков и молодых собак очень много и поверьте, что такой выбор вполне оправдан по многим причинам.', E'Подробнее.', 2, 3, 1),
    (1, 1, E'2_schedule_ru', NULL, E'В будни: с 9 до 19.\nВыходной: суббота, воскресенье.\nАдрес: ул. Демьяна Бедного, 13, Шадринск, Курганская область, 641870\nТелефон: 8 (908) 839-49-74 Гугл карты: https://goo.gl/maps/KJ6K6ydAWhjuhRqo9', E'Расписание работы, адрес и схема проезда.', 2, 3, 2),
    (1, 1, E'2_general_info_ru', NULL, E'Нахождение в приюте без пропуска грозит штрафом около 4000 рублей с человека и прекращением похода, так как госинспектор, если встретится вам на пути, будет обязан сопроводить вас за пределы охраняемой территории.\n\nСтоимость пропуска для машины в приют в этом году составляет 90 руб.\n\nДля офрмления пропуска необходимо обратиться в администрацию 1 этаж каб. 101\nтел. +79921123454\nПроуск необходимо постоянно имель при себе для предьявления сотрудникам охраны!!!', E'Оформить пропуск на машину.', 2, 3, 3),
    (1, 1, E'2_rules_ru', NULL, E'«Как себя вести при встрече с собакой»\n\nПравило №1 – Не подходи близко к собаке, находящейся на привязи.\n\nПравило №2 – Не трогай и не гладь чужих собак.\n\nПравило №3 – Не пугайся и не кричи, если к тебе бежит собака.\n\nПравило №4 – Не убегай. Остановись. Собака чаще нападает на движущегося человека.\n\nПравило №5 – Не трогай миску с пищей.\n\nПравило №6 – Не дразни собаку едой.\n\nПравило №7 – Не отбирай у собаки еду и игрушки.\n\nПравило №8 – Не трогай щенков.\n\nПравило №9 – Не подходи к незнакомой собаке.\n\nПравило №10 – Не трогай спящую собаку.\n\nПравило №11 – Не разнимай дерущихся собак.\n\nПравило №12 – Не подходи к стаям бродячих собак.\n\nПравило №13 – Не дразни собак.\n\nПравило №14 – Не позволяй собаке кусать тебя за руки.\n\nПравило №15 – Не смотри в глаза нападающей собаке.', E'Общие рекомендации по технике безопасности.', 2, 3, 4),
    (1, 1, E'2_leave_phone_ru', NULL, E'Оставьте Ваши контактные данные для связи, телефон и/или электронную почту', E'Оставить контактные данные для связи.', 2, -7, 5),
    (1, 1, E'3_back_to_shelter', NULL, E'Выберите пункт меню', E'Назад.', 3, 2, 1),
    (1, 1, E'4_meeting_rules_ru', NULL, E'Как правильно знакомиться с собакой?\nСпросите у хозяина Уточните у человека, не против ли будет он и его питомец, если вы познакомитесь. ...\nЗамедление темпа сближения ...\nДайте собаке проявить инициативу ...\nСигналы примирения ...\nДайте себя обнюхать ...\nНейтральная доброжелательность ...\nНет резким движениям ...\nИнициатива от собаки', E'Правила знакомства с собакой.', 4, 4, 0),
    (1, 1, E'4_doc_list_ru', NULL, E'Всё что вам нужно это:\n1) удостоверение личности(паспорт),\n2) заявление в установленной форме,\n3) визит волонтёра в место будующего проживания собаки.', E'Документы для заявки.', 4, 4, 1),
    (1, 1, E'4_transfer_ru', NULL, E'Для перевозки собаки по России нужен ветеринарный паспорт и ветеринарная справка по форме №1 (для перевозки животных внутри страны). Правила выдачи зависят от субъекта федерации, в котором делается запрос. Но документы на животное спрашивают нечасто. Исключение: если сделать остановку в охотничьих угодьях.', E'Как перевозить собаку.', 4, 4, 3),
    (1, 1, E'4_puppy_dog_ru', NULL, E'Как обустроить место щенка?\nПервые несколько дней, пока малыш не изучил окружающую обстановку, можно обустроить ему место в плетеной корзинке и выходом или в ящике с импровизированной дверкой. Подстилку размещайте в укромном углу комнаты, где собака и собаке никто не будет мешать.', E'Обустройство щенка.', 4, 4, 4),
    (1, 1, E'4_adult_dog_ru', NULL, E'Техника безопасности\nПервое, на что стоит обратить внимание, — это провода и окна. Провода нужно провести по верху, убрать в специальные короба или под плинтусы. Можно поставить мебель таким образом, чтобы она загораживала провода и розетки. Особенно это важно, если вы берете щенка, который в скором времени начнет все пробовать на зуб.\n\nПри доступе к окнам (например, диван стоит вплотную к подоконнику) их стоит защитить специальными решетками «антикошка». Если нижняя часть балконной решетки у вас не сплошная, то ее также лучше закрыть во избежание трагедий, поскольку у щенков не развито чувство высоты.', E'Обустройство взрослой собаки.', 4, 4, 5),
    (1, 1, E'4_disabled_dog_ru', NULL, E'Что делать, если собака простудилась\nПо словам Голубева, если наблюдается один или несколько перечисленных симптомов, необходимо обратиться к ветеринарному специалисту. Без профессионального осмотра и проверки необходимых анализов невозможно поставить точный диагноз, а самостоятельное лечение может привести к ухудшению состояния животного. \"Если у вас нет возможности отвезти собаку в ветеринарную клинику, воспользуйтесь онлайн-консультацией со специалистом\", — советует кинолог.\nДо приема у ветеринарного врача можно облегчить состояние питомца в домашних условиях. Для этого требуется обустроить собаке удобное место, где она будет защищена от сквозняков и низкой температуры. Обеспечить ей покой и теплое питье, легкую, но достаточно калорийную пищу. Если у животного лихорадка, Голубев советует укрыть его теплым покрывалом. \"Также может помочь легкий массаж гладкой щеткой\", — сказал кинолог.', E'Обустройство собаки с заболеванием.', 4, 4, 6),
    (1, 1, E'4_cynologist_tips_ru', NULL, E'Когда же следует приступать к занятиям?\n\n   К воспитанию собаки нужно приступать в ПЕРВЫЙ день появления ее в доме. Мне бы не хотелось ограничивать и усреднять всех щенков по возрасту, т.к. собака может появиться у нас и в полтора месяца, и в 11 месяцев. И все это подходит под понятие – щенок. Возраст щенка бывает разный. Главное — надо начинать с первых минут приобретения четвероногого питомца.\n\nЧто нужно отработать со щенком на этом этапе?\n\n   Основное, на что направлять занятия это на КОНТАКТ. Вам нужно найти точки соприкосновения с животным. Стать для него интересным. От вашего с ним контакта зависит вся дальнейшая жизнь, воспитание и дрессировка этого существа. Собака должна хотеть с вами общаться и взаимодействовать.\n\nКакие трудности встречает владелец на этом этапе дрессировки? Посоветуйте способы, как их преодолеть.\n\n   Самые большие трудности возникают обычно не с собакой или со щенком, а с человеком. Многие люди страдают антропоморфизмом, т.е. очеловечиванием собаки. Хозяева наделяют собаку человеческими качествами и не отдают себе отчет в том, что собаки не мыслят, как человек, не понимают, как человек, не действуют, как человек. Собака — это животное. И исходить надо из знаний законов животного мира. И с того, что они главным образом основываются на инстинктах. Помня эти законы и следуя им, вам будет', E'Советы кинолога.', 4, 4, 7),
    (1, 1, E'4_cynologist_list_ru', NULL, E'Смирнов\nИванов\nКузнецов\nСоколов\nПопов\nЛебедев\nКозлов\nНовиков\nМорозов\nПетров\nВолков\nСоловьёв\nВасильев\nЗайцев\nПавлов\nСемёнов\nГолубев\nВиноградов', E'Наши кинологи.', 4, 4, 8),
    (1, 1, E'4_refuse_reasons_ru', NULL, E'1 Большое количество животных дома 2 Нестабильные отношения в семье 3 Наличие маленьких детей 4 Съемное жилье 5 Животное в подарок или для работы\n', E'Причины отказа.', 4, 4, 9),
    (1, 1, E'4_leave_phone_ru', NULL, E'Оставьте Ваши контактные данные для связи, телефон и/или электронную почту', E'Оставить контактные данные для связи.', 4, -7, 10),
    (1, 1, E'4_volunteer', NULL, E'К вам уже спешат!!!', E'Позвать волонтера.', 4, -5, 11),
