--liquibase formatted sql

--changeset samael:100

delete from public.users;
DROP TABLE IF EXISTS public.inline_menu;
DROP TABLE IF EXISTS public.user_state;


CREATE TABLE public.user_state (
                                   id BIGSERIAL,
                                   name TEXT,
                                   tag_special TEXT,
                                   shelter_id BIGINT,
                                   CONSTRAINT user_state_pkey PRIMARY KEY(id)
)
    WITH (oids = false);

COMMENT ON TABLE public.user_state
    IS 'Статусы пользователей';

COMMENT ON COLUMN public.user_state.name
    IS 'Наименование';

COMMENT ON COLUMN public.user_state.tag_special
    IS 'Специальный статус меню';

ALTER TABLE public.user_state
    OWNER TO postgres;


ALTER SEQUENCE public.user_state_id_seq
    INCREMENT 1 MINVALUE 1
        MAXVALUE 9223372036854775807 START 1
        RESTART 20 CACHE 1
        NO CYCLE;


CREATE TABLE public.inline_menu (
                                    id BIGSERIAL,
                                    language_id BIGINT NOT NULL,
                                    shelter_id BIGINT NOT NULL,
                                    tag_callback TEXT NOT NULL,
                                    question TEXT,
                                    answer TEXT,
                                    button TEXT,
                                    state_id BIGINT NOT NULL,
                                    state_id_next BIGINT NOT NULL,
                                    priority INTEGER DEFAULT 0,
                                    CONSTRAINT inline_menu_pkey PRIMARY KEY(id),
                                    CONSTRAINT inline_menu_tag_callback_key UNIQUE(tag_callback),
                                    CONSTRAINT inline_menu_fk_shelter_id FOREIGN KEY (shelter_id)
                                        REFERENCES public.shelters(id)
                                        ON DELETE NO ACTION
                                        ON UPDATE NO ACTION
                                        NOT DEFERRABLE,
                                    CONSTRAINT inline_menu_fk_state_id FOREIGN KEY (state_id)
                                        REFERENCES public.user_state(id)
                                        ON DELETE NO ACTION
                                        ON UPDATE NO ACTION
                                        NOT DEFERRABLE,
                                    CONSTRAINT inline_menu_fk_state_id_next FOREIGN KEY (state_id_next)
                                        REFERENCES public.user_state(id)
                                        ON DELETE NO ACTION
                                        ON UPDATE NO ACTION
                                        NOT DEFERRABLE
)
    WITH (oids = false);

COMMENT ON TABLE public.inline_menu
    IS 'Генерируемое меню';

COMMENT ON COLUMN public.inline_menu.question
    IS 'Вопрос';

COMMENT ON COLUMN public.inline_menu.answer
    IS 'Ответ';

COMMENT ON COLUMN public.inline_menu.button
    IS 'Название кнопки меню';

COMMENT ON COLUMN public.inline_menu.state_id
    IS 'Статус меню';

COMMENT ON COLUMN public.inline_menu.state_id_next
    IS 'Меню следующие, по нажатию';

CREATE INDEX inline_menu_language_shelter_state ON public.inline_menu
    USING btree (language_id, shelter_id, state_id);

ALTER TABLE public.inline_menu
    OWNER TO postgres;

